import React from "react";
import { useEffect, useState } from "react";
import { useLocation, useHistory } from "react-router-dom";
import Modal from "../components/Modal";
import Layout from "../components/Layout";
import Editorr from "../components/CKEditor";
import {
  getNote,
  deleteNote,
  updateNote,
  shareNote,
} from "../apiCalls/notesApi";
import { validInput } from "../utils/validation";
const AUTOSAVE_INTERVAL = 1000;

const Note = () => {
  const history = useHistory();
  const location = useLocation();
  const id = location.pathname.split("/")[2];
  const userid = parseInt(localStorage.getItem("user_id"));

  const [note, setNote] = useState();
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [savedTitle, setSavedTitle] = useState("");
  const [savedBody, setSavedBody] = useState("");
  const [shuser, setShuser] = useState("");
  const [deleteAction, setDeleteAction] = useState(false);
  const [deleteModal, setDeleteModal] = useState(false);
  const [shareAction, setShareAction] = useState(false);
  const [shareModal, setShareModal] = useState(false);

  console.log(id + userid);
  const deleteNoteHandler = () => {
    setDeleteModal(true);
  };
  const shareNoteHandler = () => {
    setShareModal(true);
  };

  useEffect(() => {
    if (shareAction) {
      shuser = "f";
      shareNote(id, userid, shuser)
        .then((res) => {
          console.log(res);
        })
        .catch((err) => {
          alert(err);
        });
    }
  }, [shareAction]);

  useEffect(() => {
    refreshNote(id);
  }, [id]);

  const refreshNote = (id) => {
    getNote(id)
      .then((res) => {
        setNote(res);
      })
      .catch((err) => {
        alert(err);
      });
  };

  useEffect(() => {
    if (deleteAction) {
      deleteNote(note.id).then((res) => {
        history.push("/workspace");
      });
    }
  }, [deleteAction]);

  useEffect(() => {
    if (note) {
      setBody(note.body);
      setTitle(note.title);
    }
  }, [note]);

  useEffect(() => {
    const timer = setTimeout(() => {
      if ((savedTitle !== title && validInput(title)) || savedBody !== body) {
        updateNote(id, body, title).then((res) => {
          setSavedBody(body);
          setSavedTitle(title);
        });
      }
    }, AUTOSAVE_INTERVAL);
    return () => clearTimeout(timer);
  }, [title, body]);

  return (
    <Layout>
      <div className="w-full mx-auto mt-12 md:max-w-6xl">
        {deleteModal && (
          <Modal
            title="Are you sure you want to delete the note?"
            actionValue={deleteAction}
            setActionValue={setDeleteAction}
            action="Delete"
            variant="error"
            body="Are you sure you want to delete your note. This
          action cannot be undone."
            setModal={setDeleteModal}
          />
        )}
        {shareModal && (
          <Modal
            title="Are you sure you want to share this note?"
            actionValue={shareAction}
            setActionValue={setShareAction}
            action="Share"
            variant="error"
            body="Are you sure you want to share this note. This
          action cannot be undone."
            setModal={setShareModal}
          />
        )}
        <ul className="flex float-right mt-5 text-sm">
          <li>
            <div className="dropdown inline-block relative">
              <button className="bg-gray-300 text-gray-700 font-semibold py-1 px-4 rounded inline-flex items-center">
                <span className="mr-1">Share</span>
                <svg
                  className="fill-current h-4 w-4"
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 20 20"
                >
                  <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z" />{" "}
                </svg>
              </button>
              <div className=" bg-gray-200 text-gray-700 pt-1">
                <ul className="dropdown-menu inline-flex absolute hidden ">
                  <li className="rounded-b bg-gray-200 py-2 px-4 block whitespace-no-wrap inline-block">
                    <input
                      className="input input-bordered border border-purple-500 input-primary rounded "
                      type="shuser"
                      value={shuser}
                      href="#"
                      placeholder="  Write user name"
                    />
                    <button
                      type="submit"
                      className=" mx-2 bg-transparent hover:bg-purple-600 text-purple-700 font-semibold hover:text-white px-1 rounded"
 >
                      <i
                        className="fa-solid fa-share"
                        onClick={shareNoteHandler}
                      ></i>
                    </button>
                  </li>
                </ul>
              </div>
            </div>
          </li>
          <li>
            <button
              className="mx-2 bg-gray-300 text-gray-700 font-semibold py-1 px-4 rounded inline-flex items-center"
              onClick={deleteNoteHandler}
            >
              <span className="mr-1">Delete</span>
              <i className="fa-solid fa-trash"></i>
            </button>
          </li>
        </ul>

        <input
          type="text"
          value={title}
          maxLength={64}
          className="block w-full text-4xl font-bold outline-none text-primary-default"
          onChange={(e) => setTitle(e.target.value)}
        />
        <Editorr body={body} setBody={setBody} />
      </div>
    </Layout>
  );
};

export default Note;
