import axios from 'axios'

export const createNote = async (folderId) => {
    const res = await axios.put('http://localhost:5000/api/note', { folderId:folderId, title:"Untitled", body:"" }, {
      headers: {'Authorization': 'Bearer ' + localStorage.token}
  })
    return res.data
  }
  
  export const getNote = async (id) => {
    const res = await axios.get(`http://localhost:5000/api/notes/${id}`, {
      headers: {'Authorization': 'Bearer ' + localStorage.token}
  })
    return res.data
  }
  
  export const updateNote = async (id, body, title) => {
    const { data } = await axios.put(`http://localhost:5000/api/notes/${id}`, { body, title })
    return data
  }
  
  export const deleteNote = async (id) => {
    const { data } = await axios.delete(`http://localhost:5000/api/notes/${id}`)
    return data
  }
  
  export const searchNote = async (query) => {
    const { data } = await axios.post('http://localhost:5000/api/notes/search', { query })
    return data
  }
