import React from "react";
import { useEffect, useState, useContext } from "react";
import { useHistory } from "react-router-dom";
import Layout from "../components/Layout";
import { searchNote } from "../apiCalls/notesApi";

const Workspace = () => {
  const history = useHistory();
  const [query, setQuery] = useState("");
  const [notes, setNotes] = useState([]);

{/*useEffect(() => {
    searchNote(query)
      .then((res) => {
        setNotes(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [query]);*/}

  return (
    <Layout>
      <div className="mx-auto mt-5 md:w-4/5 text-">
        <h1 className="text-4xl font-bold text-primary-default">Workspace</h1>
        <input
          type="text"
          value={query}
          className="block w-full px-4 py-2 mt-5 mb-4 border-2 focus:outline-none rounded-3xl"
          placeholder="Search for notes"
          onChange={(e) => setQuery(e.target.value)}
        />
        <ul className="mt-2">
          {notes &&
            notes.map((note) => (
              <li
                key={note.id}
                className="px-3 py-3 my-3 font-medium tracking-wide cursor-pointer hover:text-green-primary hover:bg-green-light rounded-3xl"
                onClick={() => history.push(`/note/${note.id}`)}
              >
                <i className="mr-2 far fa-file-alt"></i>
                {note.title}
              </li>
            ))}
        </ul>
      </div>
    </Layout>
  );
};

export default Workspace;
