import React from "react";
import { useEffect, useRef, useState } from "react";
import { useLocation, useHistory } from "react-router-dom";
import Modal from "../components/Modal";
import Layout from "../components/Layout";
import { getShareNote} from "../apiCalls/notesApi";

const ShareNote = () => {
  const history = useHistory();
  const location = useLocation();
  const id = location.pathname.split("/")[2];

  const [note, setNote] = useState();
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [author, setAuthor] = useState("");

  useEffect(() => {
    getShareNote(id)
      .then((res) => {
        setNote(res);
      })
      .catch((err) => {
        alert(err);
      });
  }, [id]);

  useEffect(() => {
    if (note) {
      setAuthor(note.authorname);
      setTitle(note, title);
      setBody(note, body);
    }
  }, [note, id]);
  
  return (
    <Layout>
      <div className="w-full mx-auto mt-12 md:max-w-6xl">
        {deleteModal && (
          <Modal
            title="Are you sure you want to delete the note?"
            actionValue={deleteAction}
            setActionValue={setDeleteAction}
            action="Delete"
            variant="error"
            body="Are you sure you want to delete this note. This
          action cannot be undone."
            setModal={setDeleteModal}
          />
        )}
        <nav className="h-18">
          <h4>Shared by {author}</h4>
          <button
            className="flex float-right bg-color ml-4 px-3 py-1 right cursor-pointer text-white bg-gray-500 hover:bg-purple-800 transform hover:scale-10 transition-all"
            onClick={deleteNoteHandler}
          >
            Delete{" "}
          </button>
        </nav>
        <h3 className="block w-full text-4xl font-bold outline-none text-primary-default">
          {title}
        </h3>
        <p>{body}</p>
      </div>
    </Layout>
  );
};

export default ShareNote;
