import React from "react";
import { FormEvent, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { LOGIN_ROUTE, REGISTRATION_ROUTE, HOME_ROUTE } from ".../utils/consts";

const Login = () => {
  //const location = useLocation();
 // const isLogin = location.pathname === LOGIN_ROUTE;

  return (
    <div className="w-screen min-h-screen mx-auto">
      <Link to={HOME_ROUTE} className="block px-6 py-4 text-xl font-semibold">
        Home
      </Link>
      <div className="px-8 py-12 m-auto md:w-3/6 lg:w-2/6 mt-28">
        <h1 className="px-4 mb-4 text-4xl font-bold text-center">Login</h1>
        <form>
          <label>Username</label>
          <input
            className="block w-full px-3 py-1 border-2 border-primary-light"
            type="email"
            placeholder="write your username..."
            //value={login}
            //onChange={e => setLogin(e.target.value)}
          />
          <br></br>
          <label>Password</label>
          <input
            placeholder="write your password..."
            className="block w-full px-3 py-1 border-2 border-primary-light"
            type="password"
            //value={password}
            //onChange={e => setPassword(e.target.value)}
          />
          <button
            type="submit"
            className="w-full px-2 py-1 mt-4 font-medium tracking-wide text-white bg-purple-800 rounded"
          >
            Sign in
          </button>
          <p className="mt-2 font-medium">
            Don't have an account?{" "}
            <Link className="text-blue-800" to={REGISTRATION_ROUTE}>
              Sign in
            </Link>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Login;
