import axios from 'axios'

export const createNote = async (folderId) => {
    const res = await axios.put('/note', { folderId:folderId,  })
    return res.data
  }
  
  export const getNote = async (id) => {
    const res = await axios.get(`/notes/${id}`)
    return res.data
  }
  
  export const updateNote = async (id, body, title) => {
    const { data } = await axios.put(`/notes/${id}`, { body, title })
    return data
  }
  
  export const deleteNote = async (id) => {
    const { data } = await axios.delete(`/notes/${id}`)
    return data
  }
  
  export const searchNote = async (query) => {
    const { data } = await axios.post('/notes/search', { query })
    return data
  }
