import React from "react";
import {  useState , useEffect, useContext} from "react";
import { Link, useHistory } from "react-router-dom";
import { WORKSPACE_ROUTE, REGISTRATION_ROUTE, HOME_ROUTE } from "../utils/const";
import {log} from "../apiCalls/userApi"
import { Context } from "../index";
import { validLogin, validPassword } from "../utils/validation";

const Login = () => {
  const [login, setLogin] = useState('')
  const [password, setPassword] = useState('')
  const {user} = useContext(Context)


  const loginHandler = async (event) => {
    event.preventDefault();
    try {
      if (!validLogin(login)) {
        alert("Enter a valid login address");
        setLogin("");
      } else if (!validPassword(password)) {
        alert("Password should have 4 or more characters");
        setPassword("");
      } else {
        let data = await log(login, password);
        console.log(data);
        const ud = localStorage.getItem("user_id")
        const ul = localStorage.getItem("user_name")
        user.setUser(user);
        user.setName(ul);
        user.setID(ud);
        user.setIsAuth(true);
        console.log(user._isAuth)
        console.log(user._name)
        console.log(user._user)
       // console.log(user._)
        //alert("вхід!");
        history.push(WORKSPACE_ROUTE);
      }
    } catch (e) {
     alert(e);
    }
  };
  
 const history = useHistory()
  return (
    <div className="max-w-full max-h-screen ">
       
      <nav className="h-20 ">
        <div className="flex float-left mt-3 ">
        <Link to={HOME_ROUTE} className="px-6 py-5 mx-4 mt-5 text-lg font-mono  border-2 border-gray-600 rounded-lg transform transition duration-500 hover:scale-110 hover:bg-purple-400">
        Home
      </Link>
        </div>
      </nav>
    
       
    <div className="h-15 ">
    
      <div className="px-8 py-12 m-auto md:w-3/6 lg:w-2/6 mt-31">
        <h1 className="px-4 mb-4 text-4xl font-bold text-center">Login</h1>
        <form>
          <label>Username</label>
          <input
            className="block w-full px-3 py-2 text-gray-900 border border-purple-300 rounded-lg bg-gray-50 sm:text-md focus:ring-blue-500 focus:border-blue-500"
            type="login"
            placeholder="write your username..."
            value={login}
            onChange={e => setLogin(e.target.value)}
          />
          <br></br>
          <label>Password</label>
          <input
            placeholder="write your password..."
            className="block w-full px-3 py-2 text-gray-900 border border-purple-300 rounded-lg bg-gray-50 sm:text-md focus:ring-blue-500 focus:border-blue-500"
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
          <button
            type="submit"
            autoComplete="on"
            className="w-full px-2 py-1 mt-4 font-medium tracking-wide text-white bg-purple-800 rounded"
            onClick={loginHandler}
          >
            Sign in
          </button>
          <p className="mt-2 font-medium">
            Don't have an account?{" "}
            <Link className="text-purple-800" to={REGISTRATION_ROUTE}>
              Sign in
            </Link>
          </p>
        </form>
      </div>
    </div>
    </div>
  );
};

export default Login;
