import React from "react";
import { useEffect, useRef, useState } from 'react'
import { useLocation } from 'react-router-dom'
import Modal from '../components/Modal'
import Layout from '../components/Layout'
import Editor from '../components/CKEditor'
import {getNote, deleteNote, } from "../apiCalls/notesApi"
 

const Note = () => {
  const location = useLocation()
  const id = location.pathname.split('/')[2]
  const [note, setNote] = useState()
  const [title, setTitle] = useState('')
  const [body, setBody] = useState('')
  const [error, setError] = useState('')

  const [shareMenu, setShareMenu] = useState(false)
  const [email, setEmail] = useState('')
  const [deleteAction, setDeleteAction] = useState(false)
  const [deleteModal, setDeleteModal] = useState(false)
 //const [author, setAuthor] = useState<UserRole | null>(null)
  const [authenticated, setAuthenticated] = useState(false)
const history = useHistory

  const deleteNoteHandler = () => {
    setDeleteModal(true)
  }
  
  const refreshNote = (id) => {
    getNote(id)
      .then(res => {
        setNote(res)
        setError('')
      })
      .catch(err => {
        setError('Note not found')
      })
  }
 useEffect(() => {
    if (deleteAction) {
      deleteNote(note.id).then(res => {
        history.push('/workspace')
      })
    }
  }, [deleteAction])

  useEffect(() => {
    if (note) {
      setBody(note.body)
      setTitle(note.title)
    }
  }, [note])
 

  return (
    <Layout>
        
         <div className="w-full mx-auto mt-12 md:max-w-6xl"> 
         {deleteModal && (
        <Modal
          title="Are you sure you want to delete the note?"
          actionValue={deleteAction}
          setActionValue={setDeleteAction}
          action="Delete"
          variant="error"
          body="Are you sure you want to delete your note. This
          action cannot be undone."
          setModal={setDeleteModal}
        />
        )}
         <nav className="h-18">
        <ul className="flex float-right mt-5  text-sm">
          <li >
          <button className="flex float-right bg-color mr-4 px-3 py-1 right cursor-pointer text-white bg-gray-500 hover:bg-purple-800 transform hover:scale-10 transition-all "
          //onClick={deleteFolderHandler}
          >Share</button>
          </li>
          <li>
          <button className="flex float-right bg-color ml-4 px-3 py-1 right cursor-pointer text-white bg-gray-500 hover:bg-purple-800 transform hover:scale-10 transition-all"
          onClick={deleteNoteHandler}
          >Delete</button>
          </li>
        </ul>
      </nav>
        <span className="text-gray-600 mb-4">{} / {title}</span>

         <input
              type="text"
              value={title  }
              maxLength={64}
              className="block w-full text-4xl font-bold outline-none text-primary-default"
              onChange={e => setTitle(e.target.value)}
            />
            <Editor  body={body} setBody={setBody}  />
            </div> 
    </Layout>
  )
}

export default Note
