import { $authHost, $host } from "./index";
import jwt_decode from "jwt-decode";

export const registration = async (login, password) => {
  const resp = await $host.post("api/user/registration", {
    login,
    password,
  });
 
  return resp;
};
export const login = async (login, password) => {
  const resp = await $host.post("api/user/login", { login, password });
 
  return resp;
};

export const check = async () => {
  const resp  = await $authHost.get("api/user/auth");
 
  return resp;
};
