const regex = '^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$';

export const validLogin = (login) => {
    if (login.trim().length > 0 && login.match(regex)) {
        return true;
    }
    return false;
};
export const validPassword = (password) => {
    if (password.trim().length >= 4) {
        return true;
    }
    return false;
};
