import React from "react";
import { useEffect, useRef, useState } from "react";
import { useLocation, useHistory } from "react-router-dom";
import Modal from "../components/Modal";
import Layout from "../components/Layout";
import Editorr from "../components/CKEditor";
import { getNote, deleteNote, updateNote } from "../apiCalls/notesApi";
import { validInput } from "../utils/validation";
const AUTOSAVE_INTERVAL = 1000

const Note = () => {
  const location = useLocation();
  const id = location.pathname.split("/")[2];

  const [note, setNote] = useState();
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [savedTitle, setSavedTitle] = useState('')
  const [savedBody, setSavedBody] = useState('')


  const [shareMenu, setShareMenu] = useState(false);
  const [email, setEmail] = useState("");
  const [deleteAction, setDeleteAction] = useState(false);
  const [deleteModal, setDeleteModal] = useState(false);
  //const [author, setAuthor] = useState<UserRole | null>(null)
  const [authenticated, setAuthenticated] = useState(false);
  const history = useHistory();

  const deleteNoteHandler = () => {
    setDeleteModal(true);
  };

 useEffect(() => {
    refreshNote(id)
  }, [id])

  const refreshNote = (id) => {
    getNote(id)
      .then(res => {
        setNote(res)
         
      })
      .catch(err => {
        alert(err)
      })
  }

  useEffect(() => {
    if (deleteAction) {
      deleteNote(note.id).then((res) => {
        history.push("/workspace");
      });
    }
  }, [deleteAction]);

  useEffect(() => {
    if (note) {
      setBody(note.body);
      setTitle(note.title);
    }
  }, [note]);

  useEffect(() => {
    const timer = setTimeout(() => {
      if ((savedTitle !== title && validInput(title)) || savedBody !== body) {
        updateNote(id, body, title).then(res => {
          setSavedBody(body)
          setSavedTitle(title)
        })
      }
    }, AUTOSAVE_INTERVAL)
    return () => clearTimeout(timer)
  }, [title, body])

  return (
    <Layout>
      <div className="w-full mx-auto mt-12 md:max-w-6xl">
        {deleteModal && (
          <Modal
            title="Are you sure you want to delete the note?"
            actionValue={deleteAction}
            setActionValue={setDeleteAction}
            action="Delete"
            variant="error"
            body="Are you sure you want to delete your note. This
          action cannot be undone."
            setModal={setDeleteModal}
          />
        )}
        <nav className="h-18">
          <ul className="flex float-right mt-5  text-sm">
            <li>
              <button
                className="flex float-right bg-color mr-4 px-3 py-1 right cursor-pointer text-white bg-gray-500 hover:bg-purple-800 transform hover:scale-10 transition-all "
                //onClick={deleteFolderHandler}
              >
                Share
              </button>
              <div class="flex justify-center">
  <div>
    <div class="dropdown relative">
      <button
        class="
          dropdown-toggle
          px-6
          py-2.5
          bg-blue-600
          text-white
          font-medium
          text-xs
          leading-tight
          uppercase
          rounded
          shadow-md
          hover:bg-blue-700 hover:shadow-lg
          focus:bg-blue-700 focus:shadow-lg focus:outline-none focus:ring-0
          active:bg-blue-800 active:shadow-lg active:text-white
          transition
          duration-150
          ease-in-out
          flex
          items-center
          whitespace-nowrap
        "
        type="button"
        id="dropdownMenuButton1"
        data-bs-toggle="dropdown"
        aria-expanded="false"
      >
        Dropdown button
        <svg
          aria-hidden="true"
          focusable="false"
          data-prefix="fas"
          data-icon="caret-down"
          class="w-2 ml-2"
          role="img"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 320 512"
        >
          <path
            fill="currentColor"
            d="M31.3 192h257.3c17.8 0 26.7 21.5 14.1 34.1L174.1 354.8c-7.8 7.8-20.5 7.8-28.3 0L17.2 226.1C4.6 213.5 13.5 192 31.3 192z"
          ></path>
        </svg>
      </button>
      <ul
        class="
          dropdown-menu
          min-w-max
          absolute
          hidden
          bg-white
          text-base
          z-50
          float-left
          py-2
          list-none
          text-left
          rounded-lg
          shadow-lg
          mt-1
          hidden
          m-0
          bg-clip-padding
          border-none
        "
        aria-labelledby="dropdownMenuButton1"
      >
        <li>
          <a
            class="
              dropdown-item
              text-sm
              py-2
              px-4
              font-normal
              block
              w-full
              whitespace-nowrap
              bg-transparent
              text-gray-700
              hover:bg-gray-100
            "
            href="#"
            >Action</a
          >
        </li>
        <li>
          <a
            class="
              dropdown-item
              text-sm
              py-2
              px-4
              font-normal
              block
              w-full
              whitespace-nowrap
              bg-transparent
              text-gray-700
              hover:bg-gray-100
            "
            href="#"
            >Another action</a
          >
        </li>
        <li>
          <a
            class="
              dropdown-item
              text-sm
              py-2
              px-4
              font-normal
              block
              w-full
              whitespace-nowrap
              bg-transparent
              text-gray-700
              hover:bg-gray-100
            "
            href="#"
            >Something else here</a
          >
        </li>
      </ul>
    </div>
  </div>
</div>
            </li>
            <li>
              <button
                className="flex float-right bg-color ml-4 px-3 py-1 right cursor-pointer text-white bg-gray-500 hover:bg-purple-800 transform hover:scale-10 transition-all"
                onClick={deleteNoteHandler}
              >
                Delete
              </button>
            </li>
          </ul>
        </nav>
        <input
          type="text"
          value={title}
          maxLength={64}
          className="block w-full text-4xl font-bold outline-none text-primary-default"
          onChange={(e) => setTitle(e.target.value)}
        />
        <Editorr body={body} setBody={setBody}  />
      </div>
    </Layout>
  );
};

export default Note;
