import React from "react";
import { useEffect, useState } from "react";
import { useHistory, useLocation } from "react-router-dom";
import Layout from "../components/Layout/Layout";

const Folder = () => {
  const [folder, setFolder] = useState();
  const [folderLoading, setFolderLoading] = useState(true);
  const [notes, setNotes] = useState([]);
  const [folderName, setFolderName] = useState("");
  const [savedName, setSavedName] = useState("");
  const [saveLoader, setSaveLoader] = useState(false);
  const [error, setError] = useState("");
  const location = useLocation();
  const history = useHistory();

  const id = location.pathname.split('/')[2]
  {}
  useEffect(() => {
    getFolder(id)
      .then((res) => {
        setFolder(res);
        setError("");
      })
      .catch((err) => {
        setError("Folder not found");
      });

    setFolderLoading(false);
    getNotesOfFolder(id).then((res) => {
      setNotes(res);
    });
  }, [id]);

  useEffect(() => {
    if (folder) {
      setFolderName(folder.name)
    }
  }, [folder, id])

  const addNoteHandler = () => {
    addNote(id).then(res => {
      history.push(`/notes/${res?.id}`)
      getNotesOfFolder(id).then(res => {
        setNotes(res)
      })
    })
  }


  return (
    <Layout>
      <div className="max-w-6xl mx-auto mt-12">
        <input
          type="text"
          className="block w-full mb-2 text-4xl font-bold outline-none text-primary-default"
          value={folderName || ""}
          //onChange={(e) => setFolderName(e.target.value)}
          maxLength={32}
        />
        <div className="my-1">
          <button
            //onClick={addNoteHandler}
            className="py-1 px-1.5 my-2 rounded hover:bg-basic-50 text-primary-light "
          >
            <i className="mr-2 fas fa-plus"></i>
            Add Note
          </button>
        </div>
        <div className="grid mx-3 mt-4 md:mx-1 md:grid-cols-3 lg:grid-cols-4 gap-x-8 gap-y-2">
          {/*notes?.map((note) => (
            <NoteCard key={note.id} note={note} />
          ))*/}
        </div>
      </div>
    </Layout>
  );
};

export default Folder;
