import { format } from "date-fns";
import React from "react";
import { useHistory } from "react-router-dom";

const NoteCard = (
  {
    /*note*/
  }
) => {
  const note = {
    title: "hope",
    body: "fvnjc",
    updated_at: new Date(),
  };
  const history = useHistory();
  return (
    <div onClick={() => history.push("/note")}
      //onClick={() => history.push(`/notes/${note.id}`)}
      className="flex flex-col p-4 my-1  font-mono overflow-hidden bg-purple-100 transition duration-100 ease-in-out rounded-sm cursor-pointer text-primary-default h-44 shadow-custom hover:bg-basic-20 note-page"
    >
      <div className="mb-2">
        <span className="inline-block mb-1 text-xs align-bottom text-primary-light">
          <em>
            Last updated on {format(new Date(note.updated_at), "dd-MMMM")}
          </em>
        </span>
        <p className="mb-1 text-xl underline decoration-pink-500 ">{note.title}</p>
      </div>
      {!note.body && <em className="text-xs font-light ">Empty</em>}
      <div
        className="note-card text-xs text-gray-600 "
        dangerouslySetInnerHTML={{ __html: note.body }}
      ></div>
    </div>
  );
};
export default NoteCard;
