import React from "react";
import { useEffect, useState, useContext } from "react";
import { useHistory, useLocation } from "react-router-dom";
import Layout from "../components/Layout";
import NoteCard from "../components/NoteCard";
import Modal from "../components/Modal";
import { validInput } from "../utils/validation";
import {
  getShareNote,
  getAllShareNote,
 
} from "../apiCalls/noteApi";
import { createNote } from "../apiCalls/notesApi";
import { Context } from "../index";
const AUTOSAVE_INTERVAL = 1000;

const ShareNote = () => {
  const { user } = useContext(Context);
  const userName = localStorage.getItem("user_name");
  const userid = parseInt(localStorage.getItem("user_id"));

 
  const [notes, setNotes] = useState([]);
  const history = useHistory();
 

  useEffect(() => 
    getAllShareNote().then((res) => {
      setNotes(res);})
  );
 
    
 
  return (
    <Layout>
      <div className="max-w-6xl mx-auto mt-12">
        {deleteModal && (
          <Modal
            title="Are you sure you want to delete the folder?"
            actionValue={deleteAction}
            setActionValue={setDeleteAction}
            action="Delete"
            variant="error"
            body="Are you sure you want to delete your folder. This
          action cannot be undone."
            setModal={setDeleteModal}
          />
        )}

        <button
          className="flex float-right bg-color ml-4 px-3 py-1 right cursor-pointer text-white bg-gray-500 hover:bg-purple-800 transform hover:scale-10 transition-all"
          onClick={deleteFolderHandler}
        >
          Delete
        </button>

        <input
          type="text"
          className="block w-full mb-2 text-4xl font-bold outline-none text-primary-default"
          value={folderName}
          onChange={(e) => setFolderName(e.target.value)}
          maxLength={64}
        />
        <div className="my-1">
          <button
            onClick={addNoteHandler}
            className="py-1 px-1.5 my-2 rounded hover:bg-basic-50 text-primary-light "
          >
            <i className="mr-2 fas fa-plus"></i>
            Add Note
          </button>
        </div>
        <div className="grid mx-3 mt-4 md:mx-1 md:grid-cols-3 lg:grid-cols-4 gap-x-8 gap-y-2">
          {notes?.map((note) => (
            <NoteCard key={note.id} note={note} />
          ))}
        </div>
      </div>
    </Layout>
  );
};

export default ShareNote;
