import { useEffect, useState, useContext } from "react";
import { Link, useHistory } from "react-router-dom";
import { Context } from "../index";
import { createFolder, getAllFolder } from "../apiCalls/folderApi";

const Sidebar = () => {
  const { user } = useContext(Context);
  const history = useHistory();
  const userName = user.getName();
  const [folders, setFolders] = useState([]);
  const [showSidebar, setShowSidebar] = useState(false);

  useEffect(() => {
    getAllFolder().then((res) => {
      setFolders(res);
    });
  }, []);

  const createFolderHandler = async () => {
    createFolder(userName).then(res => {
      history.push(`/folder/${res?.id}`)
    })

    getAllFolder().then(res => {
      setFolders(res)
    })
   
  };

  const logOut = () => {
    user.setUser({});
    user.setIsAuth(false);
    localStorage.removeItem("token");
    history.push("/login");
  };

  
  return (
    <>
      {showSidebar ? (
        <div className="box-border w-56 min-h-screen py-4 font-medium break-all transition-all duration-200 ease-in select-none position-fixed bg-gray-200 text-primary-light">
          <div>
            {
              <p
                className="py-2 text-center cursor-pointer hover:bg-basic-50"
                onClick={() => history.push("/workspace")}
              >
                {userName}'s Workspace
              </p>
            }

            <div className="flex content-center justify-between px-2 my-3">
              <Link
                to="/"
                className="block px-3 py-1 font-medium rounded hover:bg-basic-50"
              >
                Home
              </Link>
              <button
                className="block px-3 py-1 font-medium rounded hover:bg-basic-50"
                onClick={logOut}
              >
                Logout
              </button>
              <i
                className="cursor-pointer  fas fa-chevron-left text-basic-150 "
                onClick={() => setShowSidebar(!showSidebar)}
              ></i>
            </div>
            <button
              className="block w-9/12 px-4 py-2 m-auto my-4 font-medium text-center text-white bg-purple-700 rounded hover:bg-purple-800"
              onClick={createFolderHandler}
            >
              <i className="fas fa-plus"></i> New Folder
            </button>

            <div className="px-2 py-1 my-2 text-xs font-bold uppercase cursor-pointer hover:bg-basic-50">
              <Link to={`/shared-with-me`}>Shared With me</Link>
            </div>

            <div className="px-2 py-1 mt-2 text-xs font-bold uppercase cursor-pointer hover:bg-basic-50">
              Folders
            </div>
            <>
              {folders &&
                folders.map((folder) => (
                  <Link
                    to={`/folder/${folder.id}`}
                    key={folder.id}
                    className="block px-4 py-2 my-1 hover:bg-basic-50"
                  >
                    <i className="far fa-folder-open text-basic-150"></i>{" "}
                    {folder.name}
                  </Link>
                ))}
            </>
          </div>
        </div>
      ) : (
        <div className="mt-2.5 ml-2 text-lg">
          <i
            className="cursor-pointer fas fa-bars text-basic-150"
            onClick={() => setShowSidebar(!showSidebar)}
          ></i>
        </div>
      )}
    </>
  );
};

export default Sidebar;
