import React from "react";
import { useEffect, useRef, useState } from "react";
import { useLocation, useHistory } from "react-router-dom";
import Modal from "../components/Modal";
import Layout from "../components/Layout";
import Editorr from "../components/CKEditor";
import { getNote, deleteNote, updateNote } from "../apiCalls/notesApi";
import { validInput } from "../utils/validation";
const AUTOSAVE_INTERVAL = 1000;

const Note = () => {
  const location = useLocation();
  const id = location.pathname.split("/")[2];

  const [note, setNote] = useState();
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [savedTitle, setSavedTitle] = useState("");
  const [savedBody, setSavedBody] = useState("");

  const [shareMenu, setShareMenu] = useState(false);
  const [shuser, setShuser] = useState("");
  const [deleteAction, setDeleteAction] = useState(false);
  const [deleteModal, setDeleteModal] = useState(false);
  //const [author, setAuthor] = useState<UserRole | null>(null)
  const [authenticated, setAuthenticated] = useState(false);
  const history = useHistory();

  const deleteNoteHandler = () => {
    setDeleteModal(true);
  };

  useEffect(() => {
    refreshNote(id);
  }, [id]);

  const refreshNote = (id) => {
    getNote(id)
      .then((res) => {
        setNote(res);
      })
      .catch((err) => {
        alert(err);
      });
  };

  useEffect(() => {
    if (deleteAction) {
      deleteNote(note.id).then((res) => {
        history.push("/workspace");
      });
    }
  }, [deleteAction]);

  useEffect(() => {
    if (note) {
      setBody(note.body);
      setTitle(note.title);
    }
  }, [note]);

  useEffect(() => {
    const timer = setTimeout(() => {
      if ((savedTitle !== title && validInput(title)) || savedBody !== body) {
        updateNote(id, body, title).then((res) => {
          setSavedBody(body);
          setSavedTitle(title);
        });
      }
    }, AUTOSAVE_INTERVAL);
    return () => clearTimeout(timer);
  }, [title, body]);

  return (
    <Layout>
      <div className="w-full mx-auto mt-12 md:max-w-6xl">
        {deleteModal && (
          <Modal
            title="Are you sure you want to delete the note?"
            actionValue={deleteAction}
            setActionValue={setDeleteAction}
            action="Delete"
            variant="error"
            body="Are you sure you want to delete your note. This
          action cannot be undone."
            setModal={setDeleteModal}
          />
        )}
        <nav className="h-18">
          <ul className="flex float-right mt-5  text-sm">
            <li>
              <div>
                <div className="dropdown inline-block relative">
                  <button className="bg-gray-300 text-gray-700 font-semibold py-2 px-4 rounded inline-flex items-center">
                    <span className="mr-1">Share</span>
                    <svg
                      className="fill-current h-4 w-4"
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 20 20"
                    >
                      <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z" />{" "}
                    </svg>
                  </button>
                  <div className=" bg-gray-200 text-gray-700 pt-1">
                    <ul className="dropdown-menu inline-flex absolute hidden ">
                      <li className="rounded-b bg-gray-200 py-2 px-4 block whitespace-no-wrap inline-block">
                        <input className="input input-bordered border border-purple-500 input-primary rounded "
                          type={shuser}
                          href="#"
                          placeholder="  Write user name"
                        />
                        <button type="submit" className=" mx-2 bg-transparent hover:bg-purple-600 text-purple-700 font-semibold hover:text-white   px-1 border border-purple-500 hover:border-transparent rounded">sent</button>
                      </li>
                    </ul>
                  </div>
                </div>
              </div>
            </li>
            <li>
              <button
                className="mx-2 bg-gray-300 text-gray-700 font-semibold py-2 px-4 rounded inline-flex items-center"
                onClick={deleteNoteHandler}
              >
               <span className="mr-1">Delete</span>
               <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="64" height="64" viewBox="0 0 172 172" style=" fill:#26e07f;"><g fill="none" fill-rule="nonzero" stroke="none" stroke-width="1" stroke-linecap="butt" stroke-linejoin="miter" stroke-miterlimit="10" stroke-dasharray="" stroke-dashoffset="0" font-family="none" font-weight="none" font-size="none" text-anchor="none" style="mix-blend-mode: normal"><path d="M0,172v-172h172v172z" fill="none"></path><g fill="#1fb141"><path d="M21.5,21.5v129h64.5v-32.25v-64.5v-32.25zM86,53.75c0,17.7805 14.4695,32.25 32.25,32.25c17.7805,0 32.25,-14.4695 32.25,-32.25c0,-17.7805 -14.4695,-32.25 -32.25,-32.25c-17.7805,0 -32.25,14.4695 -32.25,32.25zM118.25,86c-17.7805,0 -32.25,14.4695 -32.25,32.25c0,17.7805 14.4695,32.25 32.25,32.25c17.7805,0 32.25,-14.4695 32.25,-32.25c0,-17.7805 -14.4695,-32.25 -32.25,-32.25z"></path></g></g></svg>
              </button>
            </li>
          </ul>
        </nav>
        <input
          type="text"
          value={title}
          maxLength={64}
          className="block w-full text-4xl font-bold outline-none text-primary-default"
          onChange={(e) => setTitle(e.target.value)}
        />
        <Editorr body={body} setBody={setBody} />
      </div>
    </Layout>
  );
};

export default Note;
