import axios from 'axios'

export const getFolders = async () => {
  const res = await axios.get('/folder')
  return res.data
}

export const createFolder = async (name) => {
  const res = await axios.put('http://localhost:5000/api/folder',{
      name:name,
  },  {
    headers: {'Authorization': 'Bearer ' + localStorage.token}
})
  return res.data
}

export const getFolder = async (id) => {
  const res = await axios.get(`/folder/${id}`)
  return res.data
}

export const updateFolder = async (id, name) => {
  const res = await axios.put(`/folder/${id}`, { name })
  return res.data
}

export const deleteFolder = async (id) => {
  const res = await axios.delete(`/folder/${id}`)
  return res.data
}

export const getNoteOfFolder = async (id) => {
  const res = await axios.get(`/folder/${id}/notes`)
  return res.data
}
