import axios from 'axios'

export const getFolders = async () => {
  const res = await axios.get('http://localhost:5000/api/folder/',  {
    headers: {'Authorization': 'Bearer ' + localStorage.token}
})
  return res.data
}

export const createFolder = async () => {
  const res = await axios.put('http://localhost:5000/api/folder/' ,  {
    headers: {'Authorization': 'Bearer ' + localStorage.token}
})
  return res.data
}

export const getFolder = async (id) => {
  const res = await axios.get(`http://localhost:5000/api/folder/${id}`,  {
    headers: {'Authorization': 'Bearer ' + localStorage.token}
})
  return res.data
}

export const updateFolder = async (id, name) => {
  const res = await axios.put(`http://localhost:5000/api/folder/${id}`, { name: name },  {
    headers: {'Authorization': 'Bearer ' + localStorage.token}
})
  return res.data
}

export const deleteFolder = async (id) => {
  const res = await axios.delete(`http://localhost:5000/api/folder/${id}`,  {
    headers: {'Authorization': 'Bearer ' + localStorage.token}
})
  return res.data
}

export const getNoteOfFolder = async (id) => {
  const res = await axios.get(`http://localhost:5000/api/folder/${id}/notes`,  {
    headers: {'Authorization': 'Bearer ' + localStorage.token}
})
  return res.data
}
