import React from "react";
import { useEffect, useRef, useState } from 'react'
import { useLocation } from 'react-router-dom'
//import { getNote, updateNote } from '../apiCalls/notes'
//import { createTag, getAllTags } from '../apiCalls/tags'
 
import Layout from '../components/Layout'
import Editor from '../components/CKEditor'
//import NoteToolbar from '../components/Toolbar/Note'
 

const Note = () => {
  const location = useLocation()
  const id = location.pathname.split('/')[2]
  const [note, setNote] = useState()
  const [title, setTitle] = useState('')
  const [body, setBody] = useState('')
  const [error, setError] = useState('')
 

  const nott = {
 title:"hope",
 body:" dcfxcg bhvnbnjmk,yui/l'?:",
 FolderName: "folder"
  }
 {/*

  const refreshNote = (id) => {
    getNote(id)
      .then(res => {
        setNote(res)
        setError('')
      })
      .catch(err => {
        setError('Note not found')
      })
  }

  useEffect(() => {
    if (note) {
      setBody(note.body)
      setTitle(note.title)
    }
  }, [note])
 
*/}
  return (
    <Layout>
         <div className="w-full mx-auto mt-12 md:max-w-6xl"> 
         <nav className="h-18">
        <ul className="flex float-right mt-5  text-sm">
          <li >
          <button className="flex float-right bg-color  mr-5 px-3 py-1 right cursor-pointer text-black  "
          //onClick={deleteFolderHandler}
          >Share</button>
          </li>
          <li>
          <button className="flex float-right bg-color ml-5 px-3 py-1 right cursor-pointer text-white bg-gray-400 hover:bg-purple-800 transform hover:scale-10 transition-all"
          //onClick={deleteFolderHandler}
          >Delete</button>
          </li>
        </ul>
      </nav>
        <span className="text-gray-600 mb-4">{nott.FolderName} / {nott.title}</span>

         <input
              type="text"
              value={nott.title || ' new note'}
              maxLength={64}
              className="block w-full text-4xl font-bold outline-none text-primary-default"
              onChange={e => setTitle(e.target.value)}
            />
            <Editor  body={nott.body} setBody={setBody}  />
            </div> 
    </Layout>
  )
}

export default Note
