import React,  { useContext ,useState} from 'react';
import { Link, useHistory,useLocation } from "react-router-dom";
import { LOGIN_ROUTE, HOME_ROUTE } from "../utils/const";
import { registration } from "../apiCalls/userApi";
import {Context} from "../index";
import { validLogin, validPassword } from '../utils/validation';
import {observer} from "mobx-react-lite";
 
const Register = () => {

  const {user} = useContext(Context)
  const location = useLocation()
  const history = useHistory()
  const [error, setError] = useState('')
  const [login, setLogin] = useState('')
  const [password, setPassword] = useState('')
 

  const registrationHandler = async (event) => {
    event.preventDefault();
    try{
      if (!validLogin(login)) {
        setError('Enter a valid login address')
        return
      }
      
      if (!validPassword(password)) {
        setError('Password should have 6 or more characters')
        return
      }
      let data = await registration(login,password)
      console.log(data)
      //user.setUser(user);
      //user.setIsAuth(true);
      alert("Реєстрація успішна!")
      history.push(LOGIN_ROUTE);
    }
    catch(e){alert(e.response.data.message)}
  }
   
    return (
        <div className="w-screen min-h-screen mx-auto">
        <Link to={HOME_ROUTE} className="block px-6 py-4 text-xl font-semibold">
        Home
      </Link>
        <div className="px-8 py-12 m-auto md:w-3/6 lg:w-2/6 mt-30">
          <h1 className="px-4 mb-4 text-4xl font-bold text-center">Registration</h1>
          <form>
            <label>Username</label>
            <input
              className="block w-full px-3 py-2 text-gray-900 border border-purple-300 rounded-lg bg-gray-50 sm:text-md focus:ring-blue-500 focus:border-blue-500"
              type="login"
              placeholder="write your username..."
              value={login}
              onChange={e => setLogin(e.target.value)}
            />
            <br></br>
            <label>Password</label>
            <input
              placeholder="write your password..."
              className="block w-full px-3 py-2 text-gray-900 border border-purple-300 rounded-lg bg-gray-50 sm:text-md focus:ring-blue-500 focus:border-blue-500 "
              type="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
            <button
              type="submit"
              autocomplete="on"
              className="w-full px-2 py-1 mt-4 font-medium tracking-wide text-white bg-purple-800 rounded"
              onClick={registrationHandler}
           >
              Sign in
            </button>
            <p className="mt-2 font-medium">
            Already have an account?{' '}
              <Link className="text-purple-800" to={LOGIN_ROUTE}>
                Log in
              </Link>
            </p>
          </form>
        </div>
      </div>
    );
};

export default Register;
